definition.script:
node { stage 'Git Pull' checkout([ $class: 'GitSCM', branches: [[name: '*/master']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[url: 'https://github.com/apolloclark/discover-flask-vagrant.git']] ])
stage 'Prepare Database' dir('./www') { sh ''' python db_create_users.py python db_create_posts.py ''' }
stage 'Static Analysis' dir('./www') { sh 'pylint --generate-rcfile > .pylintrc' parallel pylint: { sh 'pylint -f parseable project/ 2>&1 | cat > pylint.out' }, nose:{ sh 'nosetests --with-xcoverage --with-xunit --all-modules --traverse-namespace --cover-package=project --cover-inclusive --cover-erase -x tests.py > /dev/null' }, failFast: true }
stage 'Deploy Servers' echo 'Deploying Testing Insfrastructure with Terrform and Ansible'
stage 'Dynamic Analysis' echo 'Running Selenium tests' echo 'Running JMeter stress tests' echo 'Running Gauntlt security tests'
stage 'Security Scan' echo 'Analysing UR cyberz!!!'
stage 'Tear-down Servers' echo 'Removing Testing Infrastructure'
stage 'Generate Reports' echo 'Generating Report Graphs and Charts' build('Python_Pipeline')
stage 'Have a Cocktail :)' echo 'Good on you! Have a cocktail. :)' }
